import React, { useState } from 'react';
import YouTube from 'react-youtube';

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [videoId, setVideoId] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const response = await fetch(`https://www.googleapis.com/youtube/v3/search?q=${searchTerm}&key='AIzaSyBSJBNXaEMPjjkuHCnN4k9bfAIuTEtO_1Q'&part=snippet&type=video&maxResults=1`);
      const data = await response.json();
      if (data.items.length === 0) {
        throw new Error('No videos found');
      }
      setVideoId(data.items[0].id.videoId);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  const handleDownload = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(`https://www.yt-download.org/api/button/mp4/${videoId}`, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      window.open(data.dl_link);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  const opts = {
    height: '390',
    width: '640',
    playerVars: {
      autoplay: 1,
    },
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Loading...' : 'Search'}
        </button>
      </form>
      {videoId && (
        <div>
          <YouTube videoId={videoId} opts={opts} />
          <button onClick={handleDownload} disabled={isLoading}>
            {isLoading ? 'Loading...' : 'Download'}
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
